mapper.select:
SELECT review_idx, user_id, restaurant_id, comment, create_date, rating
FROM review
WHERE review_idx = #{reviewIdx}

mapper.insert:
INSERT INTO review (user_id, restaurant_id, reservation_id, `comment`, create_date, rating)
SELECT r.user_id, r.restaurant_id, r.reservation_id, #{comment}, NOW(), #{rating}
FROM reservation r
WHERE r.reservation_id = #{reservationId}

mapper.update:
UPDATE review
SET rating =#{rating}, comment = #{comment}, update_date=now()
WHERE review_idx = #{reviewIdx}
